import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import { 
  PostContentStyled,
} from 'components/Style/Post'
import hljs from 'highlight.js';

const PostStyled = styled.div`
  flex: 1;
  margin: 10px 10px 30px 40px;
  padding: 10px 20px;
  /* box-shadow: 0px 10px 5px 0px rgba(240,240,240,0.8); */
`;

const Title = styled.h1`
  font-size: 1.7em;
  margin-top: 20px;
  margin-bottom: 20px;
  font-weight: 500;
  letter-spacing: 0.1em;
`;

const TagUl = styled.ul`
  margin-bottom: 0.5em;
`;

const Tag = styled.li`
  display: inline-block;
  border: 1px solid #8dc2c2;
  padding: 5px 5px;
  border-radius: 2px;
  font-size: 0.75em;
  color: #61a3a3;
  margin: 0 2px;
`;

const DateTime = styled.div`
  margin-bottom: 20px;
  color: #999;
  font-size: 1em;
`;

const Post = ({
  postList,
  post,
}) => {
  let html = post.html;
  const testHtml = document.createElement('body')
  testHtml.innerHTML = html
  let codes = testHtml.querySelectorAll('code')
  codes.forEach(code=>{
    hljs.highlightBlock(code)
  })
  let tagItems = post.tags.map(tag=>(
    <Tag key={tag}>{tag}</Tag>
  ))
  // return (
  //   <Post key={post.id}>
  //     {/* <TagUl>{tagItems}</TagUl> */}
  //     {/* <Title to={`/blog/post/${post.id}`}>{post.title}</Title> */}
  //     {/* <DateTime>{post.datetime}</DateTime> */}
  //     {/* <img src='/image/01.png'></img> */}
  //     <PostText dangerouslySetInnerHTML={{__html: testHtml.innerHTML}}/>
  //     {/* <Text>{post.text}</Text> */}
  //   </Post>
  // )
  return (
    <PostStyled>
      <TagUl>
        {tagItems}
      </TagUl>
      <Title>{post.title}</Title>
      <DateTime>{post.datetime}</DateTime>
      <PostContentStyled>
        <img src="/image/02.png" style={{maxWidth: '450px'}}/>
        <h2 class="primary">實現目標</h2>
        <iframe height="500" style={{width: '100%'}} scrolling="no" title="100DaysCss #01" src="//codepen.io/smile25896/embed/EBwNPY/?height=265&theme-id=0&default-tab=result" frameborder="no" allowtransparency="true" allowfullscreen="true">
          See the Pen <a href='https://codepen.io/smile25896/pen/EBwNPY/'>100DaysCss #01</a> by Cathy
          (<a href='https://codepen.io/smile25896'>@smile25896</a>) on <a href='https://codepen.io'>CodePen</a>.
        </iframe>
        <h2 class="primary">旋轉功能：transform: rotate(θ)</h2>
        <p>
          用<span class="highLight">transform-origin</span>設定一個選轉的中心點，再透過<span class="highLight">transform: rotate(θ)</span>設定旋轉的角度，即可達到旋轉的效果。<br/>
          θ參數必須有單位，可以是 deg (角度 ° ) 、 rad (弧度) 、 grad (梯度)。
        </p>
        <img src="/image/02-1.png" style={{maxWidth: '500px'}}/>
        <pre>
          <code className='css'>

          </code>
        </pre>
        {/* <p>

        </p>

        <h3>
          1.  聆聽鍵盤按下去(keydown)的事件
        </h3>
        <ul class="circle">
          <li>提示： <span class="highLight">HTML5 Event</span> </li>
        </ul>
        <p class="small">
          當鍵盤按下時，執行 <span class='highLight'>function playSound()</span> ，並將event裡的 <span class="highLight">keyCode</span> (會記錄按下鍵的碼)
        </p>

        <h3>
          2.  判斷按下去的按鍵，並對相應的div和audio做處理
        </h3>
        <ul class="circle">
          <li>提示：透過 <span class='highLight'>querySelector()</span> 去選擇 <span class='highLight'>data-key</span> 的屬性 </li>
        </ul>
        <p class="small">
          <span class='highLight'>function playSound()</span> ：先選取到相對應 key 的 div 和 audio <br/>
          css選擇規則中：屬性是用 [ ] 來選取，例如：div[data-key="1"] <br/>
          如果沒有對應的 audio ，則回傳，不繼續執行。 <br/>
          將選到的 div 的 class 加上 playing <br/>
          並播放audio，為了讓每次按下去都同重頭播放音效，在播放設定 <span class='highLight'>currentTime = 0;</span>
        </p>

        <h3>
          3.  鍵盤放開後，要回復原狀
        </h3>
        <p class="small">
          當transition動畫跑完時，從class中移除playing
        </p> */}
      </PostContentStyled>
      {/* <PostContentStyled key={post.id} dangerouslySetInnerHTML={{__html: testHtml.innerHTML}}/> */}
    </PostStyled>
  );
};

Post.propTypes = {
  postList: PropTypes.array.isRequired,
  post: PropTypes.object.isRequired,
};

export default Post;
