import styled from 'styled-components';

export const PostTitle = styled.h1`
  font-size: 1.7em;
  margin-top: 25px;
  margin-bottom: 25px;
  font-weight: 500;
  letter-spacing: 0.1em;
`;

export const PostSecondTitle = styled.h2`
  font-size: 1.3em;
  margin-top: 15px;
  margin-bottom: 15px;
  font-weight: 500;
  color: #61a3a3;
`;

export const Post = styled.div`
  p{
    margin-bottom: 2em;
  }
  blockquote{
    margin-bottom: 2em;
    border-left: 5px #a9d6d6 solid;
    padding-left: 20px;
  }
`;
