import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import { 
  Post,
} from 'components/Style/Post'
import marked from 'marked';
import hljs from 'highlight.js';

const HomeStyled = styled.div`
`;

const PostPreview = styled.div`
  max-width: 630px;
  padding: 60px;
  h1{
    font-size: 1.5rem;
    padding: 1em 0;
  }
  img{
    width: 100%;
  }
`;

const Title = styled.h1`

`;

const Home = ({
  postList
}) => {
  let postItems = postList.map(post=>{
    let html = post.preview;
    const testHtml = document.createElement('body')
    testHtml.innerHTML = html
    let codes = testHtml.querySelectorAll('code')
    codes.forEach(code=>{
      hljs.highlightBlock(code)
    })
    return (
      <Post 
        key={post.id}
        dangerouslySetInnerHTML={{__html: testHtml.innerHTML}}
      />
    )
  })
  return (
    <HomeStyled classNameName='container'>
      {/* {postItems} */}
      <PostPreview>
        <Title>JavaScript30：第一天筆記</Title>
        <img src='/image/01.png'></img>
      </PostPreview>
    </HomeStyled>
  );
};;

Home.propTypes = {
  postList: PropTypes.array.isRequired,
};

export default Home;
