    super(props);
    this.state = {
    }
    this.handleScroll = this.handleScroll.bind(this)
  }

  componentDidMount(){
      window.addEventListener('scroll', this.handleScroll);
  }

  handleScroll(){
    console.log(document.documentElement.scrollTop)
  }

  render() {
    return (
      <Header
