import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import { 
  PostContentStyled,
} from 'components/Style/Post'
import hljs from 'highlight.js';

const PostStyled = styled.div`
  flex: 1;
  margin: 10px 10px 30px 40px;
  padding: 10px 20px;
  /* box-shadow: 0px 10px 5px 0px rgba(240,240,240,0.8); */
`;

const Title = styled.h1`
  font-size: 1.7em;
  margin-top: 20px;
  margin-bottom: 20px;
  font-weight: 500;
  letter-spacing: 0.1em;
`;

const TagUl = styled.ul`
  margin-bottom: 0.5em;
`;

const Tag = styled.li`
  display: inline-block;
  border: 1px solid #8dc2c2;
  padding: 5px 5px;
  border-radius: 2px;
  font-size: 0.75em;
  color: #61a3a3;
  margin: 0 2px;
`;

const DateTime = styled.div`
  margin-bottom: 20px;
  color: #999;
  font-size: 1em;
`;

const Post = ({
  post,
}) => {
  post.html = `
<img src="/image/03.png" style="max-width: 450px"/>
  <h2 class="primary">實現目標</h2>
  <h3>點menu icon後，橫線會旋轉成X。</h3>
  <iframe height="500" style="width: 100%;" scrolling="no" title="100DaysCss #02 #plus" src="//codepen.io/smile25896/embed/PrJzEr/?height=265&theme-id=0&default-tab=result" frameborder="no" allowtransparency="true" allowfullscreen="true">
    See the Pen <a href='https://codepen.io/smile25896/pen/PrJzEr/'>100DaysCss #02 #plus</a> by Cathy
    (<a href='https://codepen.io/smile25896'>@smile25896</a>) on <a href='https://codepen.io'>CodePen</a>.
  </iframe>

  <h2 class="primary">詳細步驟：</h2>
  <blockquote>
    <ol>
      <li>1.  畫出動畫開頭三條線的MENU</li>
      <li>2.  透過transform製造位移，畫出動畫中三條線合併的MENU。</li>
      <li>3.  透過transform製造旋轉效果，畫出動畫結尾X形狀的MENU。</li>
      <li>4.  製作動畫，從三條線變成X。</li>
    </ol>
  </blockquote>
  
  <h2>
    1. 畫出三條線的MENU
  </h2>
  html
  <pre class='small'><code class="html">&lt;div class=&quot;frame&quot;&gt;
  &lt;div class=&quot;center&quot;&gt;
        &lt;div class=&quot;line line1&quot;&gt;&lt;/div&gt;
        &lt;div class=&quot;line line2&quot;&gt;&lt;/div&gt;
        &lt;div class=&quot;line line3&quot;&gt;&lt;/div&gt;
  &lt;/div&gt;
&lt;/div&gt;</code></pre>
  <pre><code class="scss">$menu-icon-line-height: 8px;
$menu-icon-line-width: 80px;
.line{
  background: #fff;
  width: $menu-icon-line-width;
  height: $menu-icon-line-height;
  border-radius: 3px;
  box-shadow: 0 2px 10px 0 rgba(0,0,0,0.3);
  margin: 15px 0;
}</code></pre>

  <h2>
    2.  畫出三條線合併的MENU。
  </h2>
  <p class='small'>
    透過<span class="highLight">transform: translate(x軸, y軸)</span>，將第一條和第三條線位移到第二條線的位置。
  </p>
  <h3 class="primary">位移功能：transform: translate(x軸, y軸)</h3>
  <img src='/image/03-2.png' style="max-width: 350px"/>
  <pre class='small'><code class='css'>a{ transform: translate(0, 100px); }
b{ transform: translate(100px, 0); }</code></pre>
  <pre><code class="scss">.line1{
  transform: translate(0, 23px);
}
.line2{
  /* 第二條線不需要位移 */
}
.line3{
  transform: translate(0, -23px);
}</code></pre>

<h2>
  3.  畫出X形狀的MENU。
</h2>
<pre><code class="scss">.line1{
  transform: translate(0, 23px) rotate(45deg);
}
.line2{
  /* 第二條線消失 */
  opacity: 0;
}
.line3{
  transform: translate(0, -23px) rotate(135deg);
}</code></pre>

  <h2 class="primary">CSS動畫</h2>
  <p class="small">
    透過<span class="highLight">@keyframes</span>搭配<span class="highLight">transform</span>製作CSS動畫效果。
  </p>
<pre class='small'><code class='css'>/* @keyframes 範例 */
@keyframes 動畫名稱{
  關鍵影格{ css.... }
  關鍵影格{ css.... }
}</code></pre>

  <p class="small">
    <h3>關鍵影格：</h3>
    1. 百分比數(%)<br/>
    2. from和to ：相當於0%和100%
  </p>
  <pre class='small'><code class='css'>/* 背景顏色從白變成黃再變成紅 */
@keyframes changeColor{
  0%{ background: #fff; }
  50%{ background: yellow; }
  100%{ background: red; }
}
/* 背景顏色從白直接變成紅 */
@keyframes changeColor2{
  from{ background: #fff; }
  to{ background: red; }
}</code></pre>

  <p class="small">
    不是每一個影格都要設定每個屬性，下面範例50%時沒有設定<span class="highLight">font-size</span>，CSS會自己計算。
  </p>
  <pre class='small'><code class='css'>/* 背景顏色從白變成黃再變成紅 */
@keyframes changeColor{
  0%{ 
    background: #fff;
    font-size: 10px;
  }
  50%{ 
    background: yellow;
  }
  100%{ 
    background: red;
    font-size: 20px;
  }
}</code></pre>

  <h3>
    套用動畫：動畫屬性總覽
  </h3>
  <table>
    <tbody>
      <tr>
        <td style='width: 222px'><span class="highLight">animation-name</span></td>
        <td>動畫名稱</td>
      </tr>
      <tr>
        <td><span class="highLight">animation-delay</span></td>
        <td>動畫延遲播放的時間，單位：秒(s)或毫秒(ms)，預設0秒。</td>
      </tr>
      <tr>
        <td><span class="highLight">animation-iteration-count</span></td>
        <td>動畫播放次數，預設1次。infinite代表無限次。</td>
      </tr>
      <tr>
        <td><span class="highLight">animation-timing-function</span></td>
        <td>動畫加速度函式，預設 ease，其他:linear、ease-in、ease-out、ease-in-out、step-start、step-end、steps(int,start/end)、cubic-bezier(n,n,n,n)。</td>
      </tr>
      <tr>
        <td><span class="highLight">animation-name</span></td>
        <td>動畫名稱</td>
      </tr>
    </tbody>
  <table/>
  
`;
  let html = post.html;
  const testHtml = document.createElement('body')
  testHtml.innerHTML = html
  let codes = testHtml.querySelectorAll('code')
  codes.forEach(code=>{
    hljs.highlightBlock(code)
  })
  let tagItems = post.tags.map(tag=>(
    <Tag key={tag}>{tag}</Tag>
  ))
  return (
    <PostStyled>
      <TagUl>
        {tagItems}
      </TagUl>
      <Title>{post.title}</Title>
      <DateTime>{post.datetime}</DateTime>
      <PostContentStyled key={post.id} dangerouslySetInnerHTML={{__html: testHtml.innerHTML}}/>
    </PostStyled>
  );
};

Post.propTypes = {
  postList: PropTypes.array.isRequired,
  post: PropTypes.object.isRequired,
};

export default Post;
