import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import { 
  Post,
  PostTitle,
  PostSecondTitle,
  PostThirdTitle,
  CircleUl,
  CodePre,
} from 'components/Style/Post'
import marked from 'marked';
import hljs from 'highlight.js';

const HomeStyled = styled.div`
`;

const Home = ({
  postList
}) => {
  const input = `# JavaScript30：第一天筆記

  ## 實現目標
  寫出鍵盤打擊樂。當敲擊鍵盤由A至L的位置時，頁面上的按鈕會變亮，且會發出聲音。
  
  ### 詳細步驟：
  > 1. 聆聽鍵盤按下去的事件
  > 2. 判斷按下去的按鍵，並對相應的div和audio做處理
  > 3. 鍵盤放開後，要回復原狀
  > 1. 聆聽鍵盤按下去(keydown)的事件
  > 
  * 提示： HTML5 Event
  當鍵盤按下時，執行 function playSound() ，並將event裡的 keyCode (會記錄按下鍵的碼)
  
  \`\`\`
  window.addEventListener("keydown", function(e){
    playSound(e.keyCode);
  })
  \`\`\`
  
  ### 2. 判斷按下去的按鍵，並對相應的div和audio做處理
  * 提示：透過 querySelector() 去選擇 data-key 的屬性'`;
  const output = marked(input);
  return (
    <HomeStyled classNameName='container'>
      <Post dangerouslySetInnerHTML={{__html: output}}>
      </Post>
    </HomeStyled>
  );
};;

Home.propTypes = {
  postList: PropTypes.array.isRequired,
};

export default Home;
