import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import { 
  Post,
} from 'components/Style/Post'
import marked from 'marked';
import hljs from 'highlight.js';

const HomeStyled = styled.div`
`;

const Home = ({
  postList
}) => {
  let html = postList[0].html;
  const testHtml = document.createElement('body')
  testHtml.innerHTML = html
  let codes = testHtml.querySelectorAll('code')
  // console.dir(codes)
  // codes = Array.apply(null, codes)
  codes.forEach(code=>{
    hljs.highlightBlock(code)
  })
  return (
    <HomeStyled classNameName='container'>
      <Post dangerouslySetInnerHTML={{__html: testHtml.innerHTML}}>
      </Post>
    </HomeStyled>
  );
};;

Home.propTypes = {
  postList: PropTypes.array.isRequired,
};

export default Home;
