import React, { Component } from 'react';
// import PropTypes from 'prop-types';
import Home from './Home';
import postList from 'post/list.json'

class HomeContainer extends Component {
  constructor(props){
    super(props);
    this.state = {
      md: []
    }
    this.postList = postList.slice()
    this.getPostMarkdown = this.getPostMarkdown.bind(this)
  }

  componentDidMount(){
    this.postList.forEach(post=>{
      this.getPostMarkdown(post.id)
    })
  }

  async getPostMarkdown(id){
    const file = await import(`../../post/${id}.md`);
    const response = await fetch(file.default);
    const text = await response.text();
    let md = md.slice()
    md[id] = text

    this.setState({
        md: md,
    })
  }

  render() {
    return (
      <Home
        postList={this.postList}
        md={this.state.md}
      />);
  }
}

HomeContainer.propTypes = {
};

export default HomeContainer;
